class Article extends Component {
  state = { ArticleItems: [] };

  componentDidMount() {
    this.getArticleItems();
  }

  getArticleItems() {
    axios
      .get("http://localhost:8888/api/articles/")
      .then((res) => {
        this.setState({ ArticleItems: res.data });
      })
      .catch((err) => {
        console.log(err);
      });
  }

  render() {
    return (
      <div>
        <Header />
        <ArticleNavgation />
        <div>
          {this.state.ArticleItems.map((item) => (
            <div key={item.id} style={articleStyle}>
              <h1>{item.title}</h1>
              <p>{item.content}</p>
            </div>
          ))}
        </div>
      </div>
    );
  }
}

export default Article;


const articleStyle = {
  width: '80%',
  backgroundColor: 'white',
  margin: '5% 10%'
}
