import React from "react";
import axios from "axios";
import { Form, Input, Button, Space, Radio } from "antd";
import { useState } from "react";

const { TextArea } = Input;

const topicStyle = {
  display: "flex",
  alignItems: "center",
};

const formItemLayout = {
  labelCol: {
    //label占比 当屏幕为xs最小像素时 当屏幕为sm大于小像素时
    xs: 24,
    sm: 2,
  },
  wrapperCol: {
    //input框占比
    xs: 24,
    sm: 10,
  },
};

const PostForm = (props) => {
  const handleSubmit = (event, requestType, articleID) => {
    event.preventDefault();
    const title = event.target.elements.title.value;
    const content = event.target.elements.content.value;
    const topic = event.target.elements.topics.value;

    // eslint-disable-next-line default-case
    switch (requestType) {
      case "post":
        return axios
          .post("http://localhost:8888/api/member/", {
            title: title,
            content: content,
            topic: topic,
          })
          .then((res) => console.log(res))
          .catch((err) => console.log(err));
      case "put":
        return axios
          .put("http://localhost:8888/api/member/", {
            title: title,
            content: content,
            topic: topic,
          })
          .then((res) => console.log(res))
          .catch((err) => console.log(err));
    }
  };
  return (
    <Form
      onSubmitCapture={(event) =>
        handleSubmit(event, props.requestType, props.articleID)
      }
      layout="horizontal"

    >
      <Space direction="vertical" size="middle" style={{ display: 'flex' }}>

      </Space>
      <Form.Item label="Title">
        <Input name="title" placeholder="Enter title here" />
      </Form.Item>
      <Form.Item label="TextArea">
        <TextArea rows={8} cols={80} />
      </Form.Item>
      <Form.Item label="Topic">
        <Radio.Group>
          <Radio value="Nutrition"> Nutrition </Radio>
          <Radio value="Health"> Health </Radio>
          <Radio value="Beauty"> Beauty </Radio>
        </Radio.Group>
      </Form.Item>
      <Form.Item>
        <Button type="primary" htmlType="submit">
          {props.btnText}
        </Button>
      </Form.Item>
    </Form>
  );
};
export default PostForm;

const headerStyle = {
  fontSize: "25px",
  fontFamily: "DM Sans",
};
