import React from "react";
import { Form, Input, Button } from "antd";
import axios from "axios";

const PostForm = (props) => {
  const handleSubmit = (event, requestType, articleID) => {
    event.preventDefault();
    const title = event.target.elements.title.value;
    const content = event.target.elements.content.value;
    const topic = event.target.elements.topics.value;

    // eslint-disable-next-line default-case
    switch (requestType) {
      case "post":
        return axios
          .post("http://localhost:8888/api/member/", {
            title: title,
            content: content,
            topic: topic,
          })
          .then((res) => console.log(res))
          .catch((err) => console.log(err));
      case "put":
        return axios
          .put("http://localhost:8888/api/member/", {
            title: title,
            content: content,
            topic: topic,
          })
          .then((res) => console.log(res))
          .catch((err) => console.log(err));
    }
  };
  return (
    <div>
      <Form
        onSubmitCapture={(event) =>
          handleSubmit(event, props.requestType, props.articleID)
        }
        layout="vertical"
      >
        <Form.Item
          label="Title"
          className="postArticleInput"
        >
          <Input name="title" placeholder="Enter title here" />
        </Form.Item>
        <Form.Item label="Content" className="postArticleInput">
          <Input name="content" placeholder="Enter some content ..." />
        </Form.Item>
        <Form.Item label="Topic" className="postArticleInput">
          <Input name="topic" />
        </Form.Item>
        <Form.Item>
          <Button type="primary" htmlType="submit">
            {props.btnText}
          </Button>
        </Form.Item>
      </Form>
    </div>
  );
};
export default PostForm;
