import axios from "axios";
import React, { useState, useEffect } from "react";
import ArticleNavgation from "./NavComponents/articleNav";
import Header from "./NavComponents/navigation";
import { Link } from "react-router-dom";
import { BACKEND_URL } from "./plugin";
import "../pages/Style/articleStyle.css";
import picture from "../images/food1.jpg";
import ExpertHeader from "./NavComponents/navExpertSign";

function Article() {
  const [Articles, setArticles] = useState([]);
  // const [featuredArticle, setFeaturedArticle] = useState([]);

  useEffect(() => {
    const fetchArticle = async () => {
      try {
        const res = await axios.get(`${BACKEND_URL}/api/writings/`);
        setArticles(res.data);
      } catch (error) {
        console.log(error);
      }
    };
    fetchArticle();
  }, []);

  const getArticles = () => {
    let list = [];
    let result = [];

    Articles.map((ArticlePost) => {
      return list.push(
        <div className="articleContent">
          <div className="articleItems">
            <img src={picture} alt="imagesfd" />
            <br />
            <strong className="d-inline-block mb-2 text-primary">
              Topic: {ArticlePost.category}
            </strong>

            <h3 className="mb-0">Title: {ArticlePost.title}</h3>
            <div className="mb-1 text-muted">
              Date: {ArticlePost.date_created}
            </div>
            <br />
            <Link
              to={`/article/${ArticlePost.slug}`}
              className="stretched-link"
            >
              Read the whole article!
            </Link>
          </div>
        </div>
      );
    });

    for (let i = 0; i < list.length; i += 2) {
      result.push(
        <div key={i} className="articleLayout">
          <div>{list[i]}</div>
          <div>{list[i + 1] ? list[i + 1] : null}</div>
        </div>
      );
    }

    return result;
  };
  return (
    <React.Fragment>
      <ExpertHeader />
      <ArticleNavgation />
      <main className="container">{getArticles()}</main>
    </React.Fragment>
  );
}

export default Article;
