import React, { useState, useEffect } from "react";
import Header from "./NavComponents/navigation";
import axios from "axios";
import { BACKEND_URL } from "./plugin";
import { Link, useParams } from "react-router-dom";
import "../pages/Style/articleContentsStyle.css";

function ArticleContents(props) {
  const [article, setArticle] = useState([]);
  const { id } = useParams();

  useEffect(() => {
    // const slug = props.match.params.id;
    console.log(id);

    const fetchData = async () => {
      try {
        const res = await axios.get(`${BACKEND_URL}/api/writings/${id}/`);
        setArticle(res.data);
        console.log(res.data);
      } catch (error) {
        console.log(error);
      }
    };
    fetchData();
  }, [id]);

  const createArticle = () => {
    return { __html: article.content };
  };

  return (
    <div>
      <Header />
      <div className="container">
        <h1 className="display-2">{article.title}</h1>
        <h2 className="text-muted"> {article.category}</h2>{" "}
        <h4>
          {article.month} {article.day}
        </h4>
        <div
          className="mt-5 mb-5"
          dangerouslySetInnerHTML={createArticle()}
        ></div>
        <hr />
        <p className="lead mb-5">
          <Link to="/article" className="font-weight-bold">
            Back to Blog
          </Link>
        </p>
      </div>
    </div>
  );
}

export default ArticleContents;
