import React from "react";
import axios from "axios";
import { Form, Input, Button, Space, Radio } from "antd";

const { TextArea } = Input;

const PostForm = (props) => {
  const handleSubmit = (event, requestType, articleID) => {
    event.preventDefault();
    const title = event.target.elements.title.value;
    const content = event.target.elements.content.value;
    const topic = event.target.elements.topic.value;

    // eslint-disable-next-line default-case
    switch (requestType) {
      case "post":
        return axios
          .post("http://localhost:8888/api/member/", {
            title: title,
            content: content,
            topic: topic,
          })
          .then((res) => console.log(res))
          .catch((err) => console.log(err));
      // .then(console.log(title, content, topic))
      case "put":
        return axios
          .put("http://localhost:8888/api/member/", {
            title: title,
            content: content,
            topic: topic,
          })
          .then((res) => console.log(res))
          .catch((err) => console.log(err));
    }
  };
  return (
    <Form
      onSubmitCapture={(event) =>
        handleSubmit(event, props.requestType, props.articleID)
      }
      layout="horizontal"
      wrapperCol={{
        span: 12,
      }}
    >
      <Form.Item label="Title" className="testjjj">
        <Input name="title" placeholder="Enter title here" />
      </Form.Item>
      <Form.Item label="Content">
        <TextArea
          name="content"
          rows={8}
          cols={12}
          placeholder="Enter some contents here..."
        />
      </Form.Item>
      <Form.Item label="Topic">
        <Radio.Group name="topic">
          <Radio value="Nutrition"> Nutrition </Radio>
          <Radio value="Health"> Health </Radio>
          <Radio value="Beauty"> Beauty </Radio>
        </Radio.Group>
      </Form.Item>
      <Form.Item>
        <Button type="primary" htmlType="submit">
          {props.btnText}
        </Button>
      </Form.Item>
    </Form>
  );
};
export default PostForm;

const headerStyle = {
  fontSize: "25px",
  fontFamily: "DM Sans",
};
