import React from "react";
import axios from "axios";

const topicStyle = {
  display: "flex",
  alignItems: "center",
};

const PostForm = (props) => {
  const handleSubmit = (event, requestType, articleID) => {
    event.preventDefault();
    const title = event.target.elements.title.value;
    const content = event.target.elements.content.value;
    const topic = event.target.elements.topics.value;

    // eslint-disable-next-line default-case
    switch (requestType) {
      case "post":
        return axios
          .post("http://localhost:8888/api/member/", {
            title: title,
            content: content,
            topic: topic,
          })
          .then((res) => console.log(res))
          .catch((err) => console.log(err));
      case "put":
        return axios
          .put("http://localhost:8888/api/member/", {
            title: title,
            content: content,
            topic: topic,
          })
          .then((res) => console.log(res))
          .catch((err) => console.log(err));
    }
  };
  return (
    <form onSubmitCapture={(event) =>
      handleSubmit(event, props.requestType, props.articleID)
    }>
      <div className="questionArea">
        <h1 className="title" style={headerStyle}>
          Title:
        </h1>
        <input
          type="text"
          className="iptTitle"
          placeholder="Please enter the title..."
        />
        <h1 className="content" style={headerStyle}>
          Content:
        </h1>
        <textarea
          cols="30"
          rows="8"
          placeholder="Please enter some contents..."
        ></textarea>
        <div style={topicStyle}>
          <h1 className="topic" style={headerStyle}>
            Topic:
          </h1>
          <select name="" id="">
            <option>Nutrition</option>
            <option>Health</option>
            <option>Beauty</option>
          </select>
        </div>

        <Button type="primary" htmlType="submit">
            {props.btnText}
          </Button>
      </div>
    </form>
  );
};
export default PostForm;

const headerStyle = {
  fontSize: "25px",
  fontFamily: "DM Sans",
};

