import React, { useState, useEffect } from "react";
import Header from "./NavComponents/navigation";
import axios from "axios";
import { BACKEND_URL } from "./plugin";
import { Link, useParams } from "react-router-dom";


function ArticleContents(props) {
  const [article, setArticle] = useState([]);
  const {slug} = useParams();

  useEffect(() => {

    // const slug = props.match.params.id;
    console.log(slug);

    const fetchData = async () => {
      try {
        const res = await axios.get(`${BACKEND_URL}/api/writings/${slug}/`);
        setArticle(res.data);
        console.log(res.data);
      } catch (error) {
        console.log(error);
      }
    };
    fetchData();
  }, [slug]);

  const createArticle = () => {
    return { __html: article.content };
  };

  return (
    <div className="container">
      <Header />
      <h1 className="display-2">{article.title}</h1>
      <Link to={`/article/${article.category}`}>
        <h2 className="text-muted"> {article.category}</h2>{" "}
      </Link>
      <h4>
        {article.month} {article.day}
      </h4>
      <div className="mt-5 mb-5" dangerouslySetInnerHTML={createArticle()}></div>
      <hr />
      <p className="lead mb-5">
        <Link to="/article" className="font-weight-bold">
          Back to Blog
        </Link>
      </p>
    </div>
  );
}

export default ArticleContents;
