import axios from "axios";
import React, { useState, useEffect } from "react";
import ArticleNavgation from "./NavComponents/articleNav";
import Header from "./NavComponents/navigation";
import searchIcon from "../images/search_icon.png";
import { Link } from "react-router-dom";
import { BACKEND_URL } from "./plugin";

function Blog() {
  const [blogs, setBlogs] = useState([]);
  const [featuredBlog, setFeaturedBlog] = useState([]);

  useEffect(() => {
    const fetchBlog = async () => {
      try {
        const res = await axios.get(`${BACKEND_URL}/api/writings/`);
        setBlogs(res.data);
      } catch (error) {
        console.log(error);
      }
    };
    fetchBlog();
  }, []);

  const getBlogs = () => {
    let list = [];
    let result = [];

    blogs.map((blogPost) => {
      return list.push(
        <div className="row g-0 border rounded overflow-hidden flex-md-row mb-4 shadow-sm h-md-250 position-relative">
          <div className="col p-4 d-flex flex-column position-static">
            <strong className="d-inline-block mb-2 text-primary">
              {blogPost.category}
            </strong>
            <h3 className="mb-0">{blogPost.title}</h3>
            <div className="mb-1 text-muted">{blogPost.date_created}</div>
            <p className="card-text mb-auto">{blogPost.excerpt}</p>
            <Link to={`/article/contents/${blogPost.slug}`} className="stretched-link">
              Continue reading
            </Link>
          </div>
          <div className="col-auto d-none d-lg-block">
            <img
              src={blogPost.image_cover}
              width="200"
              height="250"
              alt=""
              style={{ objectFit: "contain" }}
            />
          </div>
        </div>
      );
    });

    for (let i = 0; i < list.length; i += 2) {
      result.push(
        <div key={i} className="row mb-2">
          <div className="col-md-6">{list[i]}</div>
          <div className="col-md-6">{list[i + 1] ? list[i + 1] : null}</div>
        </div>
      );
    }

    return result;
  };
  return (
    <React.Fragment>
      <Header />
      <div className="nav-scroller py-1 mb-2">
        <nav className="nav d-flex justify-content-between">
          <Link className="p-2 link-secondary" to="/category/love">
            Love
          </Link>
          <Link className="p-2 link-secondary" to="/category/God">
            U.S.
          </Link>
          <Link className="p-2 link-secondary" to="/category/code">
            Technology
          </Link>
          <Link className="p-2 link-secondary" to="/category/love">
            Design
          </Link>
          <Link className="p-2 link-secondary" to="/category/love">
            Culture
          </Link>
          <Link className="p-2 link-secondary" to="/category/love">
            Business
          </Link>
          <Link className="p-2 link-secondary" to="/category/love">
            Politics
          </Link>
          <Link className="p-2 link-secondary" to="/category/love">
            Opinion
          </Link>
        </nav>
      </div>
      <main className="container">
        <div className="p-4 p-md-5 mb-4 text-white rounded bg-dark">
          <div className="col-md-6 px-0">
            <h1 className="display-4 fst-italic">{featuredBlog.title}</h1>
            <p className="lead my-3">{featuredBlog.excerpt}</p>
            <p className="lead mb-0">
              <Link
                to={`/article/contents/${featuredBlog.slug}`}
                className="text-white fw-bold"
              >
                Continue reading...
              </Link>
            </p>
          </div>
        </div>

        {getBlogs()}
      </main>
    </React.Fragment>
  );
}

export default Blog;

