import useItems from "antd/lib/menu/hooks/useItems";
import axios from "axios";
import React, { Component, useEffect, useState } from "react";
import Articles from "./articles";
import CustomLayout from "./components/LayoutTest";
import PostForm from "./components/postForm";
import ArticleNavgation from "./NavComponents/articleNav";
import Header from "./NavComponents/navigation";
import searchIcon from "../images/search_icon.png";
import {Link} from "react-router-dom";

class Article extends Component {
  state = { ArticleItems: [] };

  componentDidMount() {
    this.getArticleItems();
  }

  getArticleItems() {
    axios
      .get("http://localhost:8888/api/articles/")
      .then((res) => {
        this.setState({ ArticleItems: res.data });
      })
      .catch((err) => {
        console.log(err);
      });
  }

  render() {
    return (
      <div>
        <Header />
        <ArticleNavgation />
        <div>
          {this.state.ArticleItems.map((item) => (
            <div key={item.id} className="articleDetail">
              <img src={searchIcon} alt="view" />
              <Link to="/article/contents">
                <h2 style={titleStyle}>{item.title}</h2>
              </Link>

              <p>{item.content}</p>
              <div className="topic">
                <h5>Topic:Health</h5>
                <div className="rightBox">
                  <span className="view">
                    <img src={searchIcon} alt="view"></img>
                  </span>
                  <p>100</p>
                  <span className="like">
                    <img src={searchIcon} alt="like"></img>
                  </span>
                  <p>98</p>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    );
  }
}

export default Article;

const titleStyle = {
  textAlign: "center",
};
