import React from "react";
import axios from "axios";
import { Form, Input, Button, Select } from 'antd';

const topicStyle = {
  display: "flex",
  alignItems: "center",
};

const PostForm = (props) => {
  const handleSubmit = (event, requestType, articleID) => {
    event.preventDefault();
    const title = event.target.elements.title.value;
    const content = event.target.elements.content.value;
    const topic = event.target.elements.topics.value;

    // eslint-disable-next-line default-case
    switch (requestType) {
      case "post":
        return axios
          .post("http://localhost:8888/api/member/", {
            title: title,
            content: content,
            topic: topic,
          })
          .then((res) => console.log(res))
          .catch((err) => console.log(err));
      case "put":
        return axios
          .put("http://localhost:8888/api/member/", {
            title: title,
            content: content,
            topic: topic,
          })
          .then((res) => console.log(res))
          .catch((err) => console.log(err));
    }
  };
  return (
<>
      <Form
        onSubmitCapture={(event) =>
          handleSubmit(event, props.requestType, props.articleID)
        }
        layout="vertical"
      >
        <Form.Item label="Title">
          <Input name="title" placeholder="Enter title here" />
        </Form.Item>
        <Form.Item label="TextArea">
          <Input rows={8} cols={100} />
        </Form.Item>
        <Form.Item label="Topic">
          <Select>
            <Select.Option value="">Nutrition</Select.Option>
            <Select.Option value="demo">Demo</Select.Option>
            <Select.Option value="demo">Demo</Select.Option>
          </Select>
        </Form.Item>
        <Form.Item>
          <Button type="primary" htmlType="submit">
            {props.btnText}
          </Button>
        </Form.Item>
      </Form>
    </>
  );
};
export default PostForm;

const headerStyle = {
  fontSize: "25px",
  fontFamily: "DM Sans",
};



