import React from "react";
import { Form, Input, Button, Select } from "antd";
import axios from "axios";

const { TextArea } = Input;

const topicStyle = {
  display: "flex",
  alignItems: "center",
};

const PostForm = (props) => {
  const handleSubmit = (event, requestType, articleID) => {
    event.preventDefault();
    const title = event.target.elements.title.value;
    const content = event.target.elements.content.value;
    const topic = event.target.elements.topics.value;

    // eslint-disable-next-line default-case
    switch (requestType) {
      case "post":
        return axios
          .post("http://localhost:8888/api/member/", {
            title: title,
            content: content,
            topic: topic,
          })
          .then((res) => console.log(res))
          .catch((err) => console.log(err));
      case "put":
        return axios
          .put("http://localhost:8888/api/member/", {
            title: title,
            content: content,
            topic: topic,
          })
          .then((res) => console.log(res))
          .catch((err) => console.log(err));
    }
  };
  return (
    <div>
      <div className="questionArea">
        <h1 className="title">Title:</h1>
        <input
          type="text"
          className="iptTitle"
          placeholder="Please enter the title..."
        />
        <h1 className="content">Content:</h1>
        <textarea
          name=""
          id=""
          cols="30"
          rows="8"
          placeholder="Please enter some contents..."
        ></textarea>
        <div style={topicStyle}>
          <h1 className="topic">Topic:</h1>
          <select name="" id="">
            <option>Nutrition</option>
            <option>Health</option>
            <option>Beauty</option>
          </select>
        </div>

        <span className="post">Post it!</span>
      </div>
    </div>
  );
};
export default PostForm;

let headerStyle = document.getElementsByTagName("h1");
headerStyle.style.fontSize = '10px';


