import React, { useState, useEffect } from "react";
import Header from "./NavComponents/navigation";
import searchIcon from "../images/search_icon.png";
import axios from "axios";
import { BACKEND_URL } from "./plugin";
import { Link } from "react-router-dom";

function BlogDetail(props) {
  const [blog, setBlog] = useState([]);

  useEffect(() => {
    const slug = props.match.params.id;
    console.log(slug);

    const fetchData = async () => {
      try {
        const res = await axios.get(`${BACKEND_URL}/api/writings/${slug}/`);
        setBlog(res.data);
        console.log(res.data);
      } catch (error) {
        console.log(error);
      }
    };
    fetchData();
  }, [props.match.params.id]);

  const createblog = () => {
    return { __html: blog.content };
  };

  return (
    <div className="container">
      <h1 className="display-2">{blog.title}</h1>
      <Link to={`/article/contents/${blog.category}`}>
        <h2 className="text-muted"> {blog.category}</h2>{" "}
      </Link>
      <h4>
        {blog.month} {blog.day}
      </h4>
      <div className="mt-5 mb-5" dangerouslySetInnerHTML={createblog()}></div>
      <hr />
      <p className="lead mb-5">
        <Link to="/article" className="font-weight-bold">
          Back to Blog
        </Link>
      </p>
    </div>
  );
}

export default BlogDetail;

